import React, { useState, useEffect } from 'react';
import './styles.css';
import axios from 'axios';

function Todo() {
  const [todos, setTodos] = useState([]);
  const [todoInput, setTodoInput] = useState('');

  // Fetch todos from the backend
  const fetchTodos = async () => {
    try {
      const response = await axios.get('http://localhost:5000/todos');
      setTodos(response.data);
    } catch (error) {
      console.error('Error fetching todos:', error.message);
    }
  };

  useEffect(() => {
    // Fetch todos when the component mounts
    fetchTodos();
  }, []);

  // Add a new todo
  const addTodo = async () => {
    if (todoInput.trim() !== '') {
      try {
        const response = await axios.post('http://localhost:5000/todos', {
          text: todoInput,
        });
        setTodos([...todos, response.data]); // Append the new todo to the list
        setTodoInput(''); // Clear the input field
      } catch (error) {
        console.error('Error adding todo:', error.message);
      }
    }
  };

  // Remove a todo by ID
  const removeTodo = async (id) => {
    try {
      await axios.delete(`http://localhost:5000/todos/${id}`);
      const updatedTodos = todos.filter((todo) => todo._id !== id);
      setTodos(updatedTodos); // Update the state after deletion
    } catch (error) {
      console.error('Error removing todo:', error.message);
    }
  };

  return (
    <>
      <h1 id="heading">Your Task Hub</h1>

      {/* Input field and add button */}
      <div id="inputs">
        <input
          id="todo-input"
          type="text"
          placeholder="Enter your todo here"
          value={todoInput}
          onChange={(e) => setTodoInput(e.target.value)}
        />
        <button id="add-btn" onClick={addTodo}>
          <i className="fa-solid fa-plus"></i>
        </button>
      </div>

      {/* Todo list */}
      <div>
        <ul className="todo-container">
          {todos.map((todo) => (
            <li key={todo._id} className="todo-item">
              <i className="fa-solid fa-circle-check" id="check"></i>&ensp;
              <span className="todo-content">{todo.text}</span>
              <button className="remove-button" onClick={() => removeTodo(todo._id)}>
                <i className="fa-solid fa-trash"></i>
              </button>
            </li>
          ))}
        </ul>
      </div>
    </>
  );
}

export default Todo;
